import React from 'react';
import './FormCard.css';

const FormCard = (props) => {
    return (
        <div className='form-card'>

                <h4>{props.title}</h4>
                <p>{props.subtitle}</p>
                <label>{props.label}</label>
            {props.type ?     
                <input {...props.input} />
                :
                <textarea {...props.textarea} style={{width:'90%',height:''}} />
            }

        </div>
    )
}

export default FormCard
