import React from 'react'
import DetailsComponent from '../DetailsComponent/DetailsComponent'
import FormCard from './FormCard/FormCard'
import './Form.css'

const Form = () => {
    const txt = 'Lorem ipsum dolor sit amet. Vel harum obcaecati est neque quisquam est consequatur deserunt qui veniam quas non temporibus omnis qui eius quidem! Rem mollitia sunt in fugiat quia ad fuga voluptatem qui sequi commodi ex excepturi quae ut magni blanditiis. Eos corrupti Quis 33 aspernatur quisquam ut voluptatem expedita qui quia odio ut unde deleniti hic doloribus quisquam et itaque deserunt. Aut consequatur galisum At sequi veritatis et repudiandae dolorum id totam tenetur qui neque autem.';
    return (
        <DetailsComponent style={{ backgroundColor: 'rgb(219, 224, 230)' }}>
            <div className='forms'>
                <FormCard title="Question 1" type={true} subtitle={txt} input={{ type: "text", placeholder: "enter here..." }} />
                <FormCard title="Question 2" textarea={{ placeholder: "enter here..." }} />
                <FormCard title="Question 2" textarea={{ placeholder: "enter here..." }} />
            </div>
        </DetailsComponent>
    )
}

export default Form;
