import React from 'react'
import DetailsComponent from '../DetailsComponent/DetailsComponent'
import FormCard from './FormCard/FormCard'
import './Form.css';

const Form = () => {
    const txt = 'Aut explicabo cupiditate a explicabo reiciendis et sapiente voluptas sit unde enim. Harum commodi et consequatur inventore id odit odio et eius repellendus ut animi adipisci hic exercitationem quia ut sapiente galisum.';
    const label = ['first','2nd','3rd'];
    const radio = [{
        id:"n1",
        value:''
    }]

    return (
        <DetailsComponent style={{ backgroundColor: 'rgb(219, 224, 230)' }}>
            <div className='forms'>
                <FormCard title="Question 1" type={false} subtitle={txt} input={{ type: "text", placeholder: "enter here..." }} />
                <FormCard title="Question 2" type={true} label={label} input={{ type:'radio', value:'1',name:'option' ,placeholder: "enter here..." }} />
                <FormCard title="Question 3" type={false} subtitle={txt} input={{ type: "text", placeholder: "enter here..." }} />
            </div>
        </DetailsComponent>
    )
}

export default Form;
